import React from 'react';
import { connect } from 'react-redux';

import {
	clearItemFromCart,
	addItem,
	removeItem,
} from '../../redux/cart/cart.actions';

// import './checkout-item.scss';
import {
	CheckoutItemContainer,
	ImageContainer,
	CheckoutItemImg,
	CheckoutItemName,
	CheckoutItemQuantity,
	CheckoutItemPrice,
	QuantityArrow,
	QuantityValue,
	ItemRemoveButton,
} from './checkout-item.styles';

const CheckoutItem = ({ cartItem, clearItem, addItem, removeItem }) => {
	const { name, imageUrl, quantity, price } = cartItem;

	return (
		<CheckoutItemContainer>
			<ImageContainer>
				<CheckoutItemImg src={imageUrl} alt='item' />
			</ImageContainer>
			<CheckoutItemName>{name}</CheckoutItemName>

			<CheckoutItemQuantity className='quantity'>
				<QuantityArrow className='arrow' onClick={() => removeItem(cartItem)}>
					&#10094;
				</QuantityArrow>
				<QuantityValue className='value'>{quantity}</QuantityValue>
				<QuantityArrow className='arrow' onClick={() => addItem(cartItem)}>
					&#10095;
				</QuantityArrow>
			</CheckoutItemQuantity>

			<CheckoutItemPrice className='price'>{price}</CheckoutItemPrice>
			<ItemRemoveButton
				className='remove-button'
				onClick={() => clearItem(cartItem)}
			>
				&#10005;
			</ItemRemoveButton>
		</CheckoutItemContainer>
	);
};

const mapDispatchToProps = (dispatch) => ({
	clearItem: (item) => dispatch(clearItemFromCart(item)),
	addItem: (item) => dispatch(addItem(item)),
	removeItem: (item) => dispatch(removeItem(item)),
});

export default connect(null, mapDispatchToProps)(CheckoutItem);
