import React from 'react';
import { Route, Switch } from 'react-router-dom';

import './App.css';

import HomePage from './pages/homepage/homepage';
import ShopPage from './pages/shop/shop';
import SignInAndSignUpPage from './pages/sign-in-and-sign-out/sign-in-and-sign-out';
import Header from './components/header/header';

import { auth, createUserProfileDocument } from './firebase/firebase.utils';

export default class App extends React.Component {
	constructor() {
		super();

		this.state = {
			currentUser: null,
		};
	}

	unsubscribeFromAuth = null;

	componentDidMount() {
		this.unsubscribeFromAuth = auth.onAuthStateChanged(async (userAuth) => {
			if (userAuth) {
				// Если user есть и его данных нет в DB, тогда добавит в DB и вернет userRef. Если user есть в DB, тогда просто вернет userRef.
				const userRef = createUserProfileDocument(userAuth);
				// onSnapshot следит за изменением данных и при этом возвращает snapshot (из которого можно получить - id и затем, с помощью метода .get() - данные пользователя.)
				userRef.onSnapshot((snapShot) => {
					this.setState({
						currentUser: {
							id: snapShot.id,
							...snapShot.data(),
						},
					});
				});
				console.log(this.state);
			} else {
				this.setState({
					currentUser: null,
				});
			}
		});
	}

	componentWillUnmount() {
		this.unsubscribeFromAuth();
	}

	render() {
		return (
			<div>
				<Header currentUser={this.state.currentUser} />
				<Switch>
					<Route exact path='/' component={HomePage} />
					<Route path='/shop' component={ShopPage} />
					<Route path='/signin' component={SignInAndSignUpPage} />
				</Switch>
			</div>
		);
	}
}
