import React from 'react';
import { connect } from 'react-redux';

import { addItem } from '../../redux/cart/cart.actions';

import {
	ItemContainer,
	ImageContainer,
	FooterContainer,
	FooterName,
	FooterPrice,
	CollectionButton,
} from './collection-item.styles';

const CollectionItem = ({ item, addItem }) => {
	const { name, price, imageUrl } = item;
	return (
		<ItemContainer className='collection-item'>
			<ImageContainer
				className='image'
				style={{ backgroundImage: `url(${imageUrl})` }}
			/>
			<FooterContainer className='collection-footer'>
				<FooterName className='name'>{name}</FooterName>
				<FooterPrice className='price'>{price}</FooterPrice>
			</FooterContainer>
			<CollectionButton inverted onClick={() => addItem(item)}>
				Add to cart
			</CollectionButton>
		</ItemContainer>
	);
};

const mapDispatchToProps = (dispatch) => ({
	addItem: (item) => dispatch(addItem(item)),
});

export default connect(null, mapDispatchToProps)(CollectionItem);
