import { combineReducers } from 'redux';
import { persistReducer } from 'redux-persist';
import storage from 'redux-persist/lib/storage';

import userReducer from './user/user.reducer';
import cartReducer from './cart/cart.reducer';

const persistConfig = {
	// From what point inside of our root-reducer we want to start storing everything.
	key: 'root',
	// The kind of storage. (LocalStorage).
	storage,
	// An array of reducers that we want to store. user is handled by firebase, so there is no need to persist it. Here we want to persist only cart reducer.
	whitelist: ['cart'],
};

const rootReducer = combineReducers({
	user: userReducer,
	cart: cartReducer,
});

export default persistReducer(persistConfig, rootReducer);
