import firebase from 'firebase/app';
import 'firebase/firestore';
import 'firebase/auth';

const config = {
	apiKey: 'AIzaSyAb_MCmAQQZJAauN7G2-hAUSYwn4tRSD8I',
	authDomain: 'crown-db-9467d.firebaseapp.com',
	databaseURL: 'https://crown-db-9467d.firebaseio.com',
	projectId: 'crown-db-9467d',
	storageBucket: 'crown-db-9467d.appspot.com',
	messagingSenderId: '169832412267',
	appId: '1:169832412267:web:806bfa2256c8187ba6ed2e',
	measurementId: 'G-KJV40E62XX',
};

// Сохраняем пользователя в DB.
export const createUserProfileDocument = async (userAuth, additionalData) => {
	// Если объект пользователя есть, тогда выполнится.
	if (!userAuth) return;
	// firestore возвращает два вида объектов: QueryReference or QuerySnapshot. Они могут быть либо документом, либо коллекцией в зависимости от того какой именно запрашиваем. Ответ получим, даже, если данных в DB нет.

	// Query - запрос на получение документа/коллекции из DB:
	// firestore.doc('/users/:userId');
	// firestore.collection('/users');

	// QueryReference (firestore.doc('users/dsfasfsafas') - отправили запрос на получение данных - получим в ответ объект QueryReference. Этот объект не содержит данных, касающихся документа/коллекции, которую запрашиваем. Он содержит данные запроса (path и id, который передали) и метод для получения объекта Snapshot, который содержит данные, которые ищем.

	// documentReference (запрос к документу) - к документу можно применить CRUD методы (create - .set(), retrieve - .get(), update - .update(), delete - .delete() ).

	// .get() - получить данные из documentReference - применяем на объекте, который возвращает documentReference - возвращает documentSnapshot (объект с данными, касающимися нашего запроса). Этот метод можно применить и на коллекции (Проверить, что получим).

	// collectionReference (запрос к коллекции) - к коллекции можно применить .add() метод, позволяющий добавить документ в коллекцию.

	const userRef = firestore.doc('users/dsfasfsafas');
	// QuerySnapshot -
};

firebase.initializeApp(config);

export const auth = firebase.auth();
export const firestore = firebase.firestore();

const provider = new firebase.auth.GoogleAuthProvider();
provider.setCustomParameters({ prompt: 'select_account' });
export const signInWithGoogle = () => auth.signInWithPopup(provider);

export default firebase;
