import React from 'react';
import { connect } from 'react-redux';

// import CustomButton from '../custom-button/custom-button';
import { addItem } from '../../redux/cart/cart.actions';

// import './collection-item.scss';
import {
	CollectionItemContainer,
	ImageContainer,
	CollectionFooterContainer,
	CollectionFooterName,
	CollectionFooterPrice,
	CollectionButton,
} from './collection-item.styles';

const CollectionItem = ({ item, addItem }) => {
	const { name, price, imageUrl } = item;
	return (
		<CollectionItemContainer className='collection-item'>
			<ImageContainer
				className='image'
				style={{ backgroundImage: `url(${imageUrl})` }}
			/>
			<CollectionFooterContainer className='collection-footer'>
				<CollectionFooterName className='name'>{name}</CollectionFooterName>
				<CollectionFooterPrice className='price'>{price}</CollectionFooterPrice>
			</CollectionFooterContainer>
			<CollectionButton inverted onClick={() => addItem(item)}>
				Add to cart
			</CollectionButton>
		</CollectionItemContainer>
	);
};

const mapDispatchToProps = (dispatch) => ({
	addItem: (item) => dispatch(addItem(item)),
});

export default connect(null, mapDispatchToProps)(CollectionItem);
